$(document).ready(function(){var url=window.location.href;var loaded_video=url.split('?')[1];var comment_count=0;var comment_loaded=0;var intervalId=setInterval(function(){checkURL(window.location.href);if($('ytd-comment-thread-renderer').length){comment_loaded=$('ytd-comment-thread-renderer').length;if(comment_count!==comment_loaded){comment_count=comment_loaded;checkLoaded()}}else{}},1000);function checkURL(url){var newURL=url.split('?')[1];if(newURL!==loaded_video){$('.style-scope ytd-comment-renderer').parent().css('border','none');$('.style-scope ytd-comment-renderer').parent().show();loaded_video=newURL;checkLoaded()}}
function checkLoaded(){$('.style-scope ytd-comment-renderer').each(function(){text=$(this).prop('innerText');split=text.split('\n')[2];split=split.toLowerCase();if(/(19[0123456789]\d|20[0123456789]\d)/.test(split)){$(this).parent().css('border','2px solid blue');$(this).parent().hide()}})}})